CREATE VIEW TaxiPartitioningView AS
SELECT
result.*,result.filepath(1) as yearmonth
FROM
    OPENROWSET(
            BULK '/data/parquetfiles/green_tripdata_*.*',
            DATA_SOURCE='ContosoLake',
            FORMAT='PARQUET'
    ) AS [result];

select TOP 100 * from TaxiPartitioningView where yearmonth = '2022-11';
